import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Trips() {
    const [trips, setTrips] = useState([]);
    const [newTrip, setNewTrip] = useState({
        id: '',
        destination: '',
        description: '',
        price: '',
        image_url: ''
    });
    const [editingTrip, setEditingTrip] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetchTrips();
    }, []);

    const fetchTrips = () => {
        fetch('http://localhost:3000/trips')
            .then((res) => res.json())
            .then((data) => setTrips(Array.isArray(data) ? data : []))
            .catch((err) => console.error(err));
    };

    const addTrip = (event) => {
        event.preventDefault();
        fetch('http://localhost:3000/trips', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newTrip),
        })
            .then((res) => res.json())
            .then(() => {
                setNewTrip({
                    id: '',
                    destination: '',
                    description: '',
                    price: '',
                    image_url: ''
                });
                fetchTrips();
            })
            .catch((err) => console.error(err));
    };

    const startEditing = (trip) => {
        setEditingTrip(trip);
    };

    const updateTrip = (event) => {
        event.preventDefault();
        if (!editingTrip) return;

        fetch(`http://localhost:3000/trips/${editingTrip.id}`,
            {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(editingTrip),
            })
            .then((res) => res.json())
            .then(() => {
                setEditingTrip(null);
                fetchTrips();
            })
            .catch((err) => console.error(err));
    };

    const deleteTrip = (id) => {
        fetch(`http://localhost:3000/trips/${id}`,
            {
                method: 'DELETE',
            })
            .then((res) => res.json())
            .then(() => fetchTrips())
            .catch((err) => console.error(err));
    };

    return (
        <div style={styles.pageContainer}>
            <button onClick={() => navigate('/')}>← Nazad na početnu</button>
            <h1>Putovanja</h1>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '20px' }}>
                {trips.map(trip => (
                    <div key={trip.id} style={{ border: '1px solid #ccc', padding: '10px', borderRadius: '8px' }}>
                        {/* PRIKAZ SLIKE (Zahtjev br. 2) */}
                        <img
                            src={trip.image_url || 'https://via.placeholder.com/150'}
                            alt={trip.destination}
                            style={{ width: '100%', height: '150px', objectFit: 'cover' }}
                        />
                        <h3>{trip.destination}</h3>
                        <p>{trip.description}</p>
                        <p><strong>Cijena:</strong> {trip.price} KM</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Trips;
